import 'package:flutter/material.dart';
import 'single_item_page.dart';

class GridviewPage extends StatelessWidget {
  const GridviewPage({super.key});

  @override
  Widget build(BuildContext context) {
    var fruits = [
      {"img": "data:image/jpeg;base64,", "title": "Apple"},
      {"img": "data:image/jpeg;base64,", "title": "Banana"},
      {"img": "data:image/jpeg;base64,", "title": "Orange"},
      {"img": "data:image/jpeg;base64,", "title": "Grapes"},
      {"img": "data:image/jpeg;base64,", "title": "Mango"},
      {"img": "data:image/jpeg;base64,", "title": "Pineapple"},
    ];

    return Scaffold(
      backgroundColor: Colors.lightBlue[50],
      appBar: AppBar(
          title: const Text("Fruits GridView"), backgroundColor: Colors.blue),
      body: GridView.builder(
        gridDelegate: const SliverGridDelegateWithFixedCrossAxisCount(
          crossAxisCount: 2,
        ),
        itemCount: fruits.length,
        itemBuilder: (context, index) {
          return GestureDetector(
            onTap: () {
              Navigator.push(
                context,
                MaterialPageRoute(
                  builder: (context) => SingleItemPage(
                    image: fruits[index]['img']!,
                    title: fruits[index]['title']!,
                  ),
                ),
              );
            },
            child: Card(
              color: Colors.blue[100],
              child: Column(
                mainAxisAlignment: MainAxisAlignment.center,
                children: [
                  Image.network(
                    fruits[index]['img']!,
                    width: 100,
                    height: 100,
                    fit: BoxFit.cover,
                  ),
                  const SizedBox(height: 10),
                  Text(
                    fruits[index]['title']!,
                    style: const TextStyle(
                        fontSize: 18, fontWeight: FontWeight.bold),
                  ),
                ],
              ),
            ),
          );
        },
      ),
    );
  }
}
